CREATE TABLE Airline_info (
    airline_id serial PRIMARY KEY,
    airline_code VARCHAR(30),
    airline_name VARCHAR(50) NOT NULL,
    airline_country VARCHAR(50) NOT NULL,
    created_at TIMESTAMP NOT NULL,
    update_at TIMESTAMP
);
SELECT * FROM airline;

CREATE TABLE airport (
    airport_id serial PRIMARY KEY,
    airport_name VARCHAR(50) NOT NULL,
    country VARCHAR(50) NOT NULL,
    state VARCHAR(50) NOT NULL,
    city VARCHAR(50) NOT NULL,
    created_at TIMESTAMP NOT NULL,
    update_at TIMESTAMP
);






CREATE TABLE baggage_check (
    baggage_check_id serial PRIMARY KEY,
    check_result VARCHAR(50) NOT NULL,
    created_at TIMESTAMP NOT NULL,
    update_at TIMESTAMP,
    booking_id INT NOT NULL,
    passenger_id INT NOT NULL,
    FOREIGN KEY (passenger_id) REFERENCES passengers(passenger_id),
    FOREIGN KEY (booking_id) REFERENCES booking(booking_id)
);



CREATE TABLE baggage (
    baggage_id serial PRIMARY KEY,
    weight_in_kg DECIMAL(4, 2) NOT NULL,
    created_at TIMESTAMP NOT NULL,
    update_at TIMESTAMP,
    booking_id INT NOT NULL,
    FOREIGN KEY (booking_id) REFERENCES booking(booking_id)
);



DROP TABLE boarding_pass;
CREATE TABLE boarding_pass (
    boarding_pass_id serial PRIMARY KEY,
    booking_id INT,
    seat VARCHAR(50) NOT NULL,
    boarding_time TIMESTAMP NOT NULL,
    created_at TIMESTAMP NOT NULL,
    update_at TIMESTAMP,
    FOREIGN KEY (booking_id) REFERENCES booking(booking_id)
);

DROP TABLE booking_flight;
CREATE TABLE booking_flight (
    booking_flight_id serial PRIMARY KEY,
    booking_id INT NOT NULL,
    flight_id INT NOT NULL,
    created_at TIMESTAMP NOT NULL,
    update_at TIMESTAMP,
    FOREIGN KEY (booking_id) REFERENCES booking(booking_id),
    FOREIGN KEY (flight_id) REFERENCES flights(flight_id)
);


CREATE TABLE booking (
    booking_id serial PRIMARY KEY,
    flight_id INT,
    passenger_id INT NOT NULL,
    booking_platform VARCHAR(50) NOT NULL,
    created_at TIMESTAMP NOT NULL,
    update_at TIMESTAMP,
    status VARCHAR(50) NOT NULL,
    price DECIMAL(7,2),
    FOREIGN KEY (passenger_id) REFERENCES passengers(passenger_id)
);


ALTER TABLE booking RENAME COLUMN ticket_price TO price;
CREATE TABLE flights (
	flight_id serial PRIMARY KEY,
	flight_no VARCHAR(50),
	scheduled_departure DATE,
	scheduled_arrival DATE,
	departure_airport_id INT,
	arrival_airport_id INT,
	departing_gate VARCHAR(50),
	arriving_gate VARCHAR(50),
	airline_id INT,
	status VARCHAR(50),
	actual_departure DATE,
	actual_arrival DATE,
	created_at DATE,
	update_at DATE,
    FOREIGN KEY (departure_airport_id) REFERENCES airport(airport_id),
    FOREIGN KEY (arrival_airport_id) REFERENCES airport(airport_id),
    FOREIGN KEY (airline_id) REFERENCES airline(airline_id)
);


CREATE TABLE passengers (
    passenger_id serial PRIMARY KEY,
    first_name VARCHAR(50) NOT NULL,
    last_name VARCHAR(50) NOT NULL,
    date_of_birth DATE NOT NULL,
    gender VARCHAR(50) NOT NULL,
    country_of_citizenship VARCHAR(50) NOT NULL,
    country_of_residence VARCHAR(50) NOT NULL,
    passport_number VARCHAR(20) NOT NULL,
    created_at TIMESTAMP NOT NULL,
    update_at TIMESTAMP
);
SELECT * FROM passengers;



CREATE TABLE security_check (
    security_check_id serial PRIMARY KEY,
    check_result VARCHAR(20) NOT NULL,
    created_at TIMESTAMP NOT NULL,
    update_at TIMESTAMP,
    passenger_id INT NOT NULL,
    FOREIGN KEY (passenger_id) REFERENCES passengers(passenger_id)
);
SELECT * FROM security_check;
DROP TABLE security_check;


