CREATE VIEW all_details AS
    SELECT * FROM flights
    WHERE EXTRACT(YEAR FROM actual_departure)=2024;


CREATE VIEW next_week AS
    SELECT booking.* FROM booking JOIN flights
        ON flights.flight_id=booking.flight_id
        WHERE EXTRACT(DAYS FROM AGE(scheduled_departure, current_date))<=7;
SELECT * FROM next_week;
SELECT * FROM all_details;



CREATE VIEW populatflights AS
    SELECT booking_flight.flight_id, COUNT(*) AS top FROM booking_flight
    GROUP BY booking_flight.flight_id
    ORDER BY top DESC
    LIMIT 5;


CREATE VIEW flights_airline AS
    SELECT * FROM flights
             WHERE airline_id BETWEEN 5 AND 10;

CREATE OR REPLACE VIEW flights_airline AS
    SELECT * FROM flights
             WHERE (airline_id BETWEEN 5 AND 10)
               AND (actual_arrival BETWEEN current_date
                   AND current_date + INTERVAL '7 days');

