SELECT * FROM populatflights;
SELECT * FROM flights_airline;

CREATE VIEW more_than_day AS
    SELECT * FROM flights
             WHERE actual_arrival BETWEEN current_date
                 AND current_date + INTERVAL '1 day';



CREATE VIEW who_made AS
    SELECT passengers.first_name, passengers.last_name FROM passengers
        JOIN booking
            ON booking.passenger_id=passengers.passenger_id
                                WHERE booking.booking_platform = 'Skyscanner';




CREATE OR REPLACE VIEW top_countries AS
    SELECT airline.airline_country, COUNT(flights.airline_id) as top FROM flights
       JOIN airline ON flights.airline_id = airline.airline_id
                            GROUP BY airline.airline_country
                            ORDER BY top DESC
                            LIMIT 10;
SELECT * FROM top_countries;
